import BaseTarget from "./BaseTarget.js";
import packageVersions from "../cli/package-versions.js";
import {Section} from '@ltd/j-toml';

let NETLIFY_STUB=`
//
// This file is automatically generated.
//
// Don't edit this file!
//

import * as mod from "$ENTRYPOINT";
import {MikrokatServer} from "mikrokat";

let server=new MikrokatServer({mod: mod});

export default async function(request) {
	return await server.handleRequest({
		request: request
	});
}

export const config = { path: "/*" };
`;

export default class NetlifyTarget extends BaseTarget {
	constructor(arg) {
		super(arg);
	}

	async build() {
		await this.cli.processProjectFile("public/404.html",null,async text=>{
			return "Not Found."
		});

		await this.cli.writeStub("netlify/edge-functions/entrypoint.netlify.js",NETLIFY_STUB);
	}

	async init() {
		await this.cli.processProjectFile("package.json","json",async pkg=>{
			if (!pkg.scripts) pkg.scripts={};
			if (!pkg.scripts["dev:netlify"])
				pkg.scripts["dev:netlify"]="TARGET=netlify mikrokat build && netlify dev --cwd=."

			if (!pkg.scripts["deploy:netlify"])
				pkg.scripts["deploy:netlify"]="TARGET=netlify mikrokat build && netlify deploy --cwd=."

			if (!pkg.dependencies) pkg.dependencies={};
			pkg.dependencies["netlify-cli"]="^"+packageVersions["netlify-cli"];

			return pkg;
		});

		await this.cli.processProjectFile("netlify.toml","toml",async netlify=>{
			if (!netlify)
				netlify={};

			if (!netlify.build) netlify.build=Section({});
			netlify.build.publish="public";
			netlify.build.edge_functions = "netlify/edge-functions";
			netlify.build.command="";

			if (!netlify.edge_functions) {
				netlify.edge_functions=[Section({
					path: "/*",
					function: "entrypoint.netlify"
				})];
			}

			return netlify;
		});


		await this.cli.processProjectFile(".gitignore","lines",async ignore=>{
			if (!ignore.includes(".netlify")) ignore.push(".netlify");
			if (!ignore.includes("netlify")) ignore.push("netlify");
		});

		this.cli.log("Netlify initialized. Start a dev server with:");
		this.cli.log();
		this.cli.log("  npm run dev:netlify");
		this.cli.log();
		this.cli.log("Deploy with:");
		this.cli.log();
		this.cli.log("  npm run deploy:netlify");
		this.cli.log();
	}
}